// ========================================================
// QUANTUM DATA ORCHESTRATOR v6.0 - CON AN√ÅLISIS DE ART√çCULO EXTERNO
// ========================================================

class QuantumOrchestratorV6 {
  constructor() {
    this.startTime = Date.now();
    this.currentYear = 2025;
    this.errors = [];
    this.warnings = [];
  }

  // Fix encoding
  fixEncoding(text) {
    if (!text) return '';
    return String(text)
      .replace(/√É¬±/g, '√±').replace(/√É'/g, '√ë').replace(/√É¬°/g, '√°')
      .replace(/√É¬©/g, '√©').replace(/√É¬≠/g, '√≠').replace(/√É¬≥/g, '√≥')
      .replace(/√É¬∫/g, '√∫').replace(/√É/g, '√Å').replace(/√É‚Ä∞/g, '√â')
      .replace(/√É/g, '√ç').replace(/√É"/g, '√ì').replace(/√É≈°/g, '√ö')
      .replace(/√Ç¬ø/g, '¬ø').replace(/√Ç¬°/g, '¬°');
  }

  // Analizar art√≠culo externo para extraer insights
  analyzeExternalArticle() {
    try {
      const externalData = $items('Load External Article')[0]?.json;
      if (!externalData || !externalData.data) return null;

      const content = this.fixEncoding(externalData.data);

      // Extraer elementos clave del art√≠culo
      const analysis = {
        hasContent: true,
        length: content.length,

        // Detectar problemas encontrados en los comentarios
        issues: {
          outdatedSources: [],
          fictionalExamples: [],
          externalRecommendations: [],
          inappropriateTerms: [],
          inconsistencies: []
        },

        // Buscar fechas outdated (antes de 2025)
        checkOutdatedDates: () => {
          const datePattern = /\b(202[0-4])\b/g;
          const matches = content.match(datePattern) || [];
          matches.forEach(date => {
            if (parseInt(date) < 2025) {
              analysis.issues.outdatedSources.push({
                found: date,
                correction: '2025',
                context: content.substring(content.indexOf(date) - 50, content.indexOf(date) + 50)
              });
            }
          });
        },

        // Detectar ejemplos no ficticios
        checkFictionalExamples: () => {
          const namePattern = /(?:Mar√≠a|Carlos|Jos√©|Ana|Carmen)(?!.*imaginemos)(?!.*ficticio)/gi;
          const matches = content.match(namePattern) || [];
          matches.forEach(name => {
            analysis.issues.fictionalExamples.push({
              found: name,
              correction: `Imaginemos a ${name}`,
              note: 'Debe aclarar que es un ejemplo ficticio'
            });
          });
        },

        // Detectar recomendaciones externas
        checkExternalRecommendations: () => {
          const competitors = [
            'Vanguard', 'Fidelity', 'Charles Schwab', 'Betterment', 
            'Wealthfront', 'Fundrise', 'RealtyMogul'
          ];
          competitors.forEach(comp => {
            if (content.includes(comp)) {
              analysis.issues.externalRecommendations.push({
                found: comp,
                correction: 'Herramientas de Finhabits',
                note: 'Solo recomendar herramientas internas'
              });
            }
          });
        },

        // Detectar t√©rminos inapropiados
        checkInappropriateTerms: () => {
          const inappropriate = ['indocumentado', 'ilegal', 'sin papeles'];
          inappropriate.forEach(term => {
            if (content.toLowerCase().includes(term)) {
              analysis.issues.inappropriateTerms.push({
                found: term,
                correction: 'personas que necesitan ITIN o SSN',
                note: 'Usar lenguaje inclusivo y profesional'
              });
            }
          });
        },

        // Extraer estructura del art√≠culo
        extractStructure: () => {
          return {
            title: (content.match(/^#\s+(.+)/m) || ['', ''])[1],
            h2Sections: (content.match(/^##\s+.+/gm) || []).map(h => h.replace(/^##\s+/, '')),
            h3Sections: (content.match(/^###\s+.+/gm) || []).map(h => h.replace(/^###\s+/, '')),
            faqs: (content.match(/\*\*¬ø[^?]+\?\*\*/g) || []),
            hasDisclaimer: content.includes('asesor√≠a financiera'),
            wordCount: content.split(/\s+/).length
          };
        },

        // Extraer contenido √∫til para reutilizar
        extractUsefulContent: () => {
          return {
            goodExamples: [],
            dataPoints: [],
            structure: analysis.extractStructure(),
            tone: 'educational',
            style: 'clear and accessible'
          };
        }
      };

      // Ejecutar todos los an√°lisis
      analysis.checkOutdatedDates();
      analysis.checkFictionalExamples();
      analysis.checkExternalRecommendations();
      analysis.checkInappropriateTerms();
      const useful = analysis.extractUsefulContent();

      return {
        hasExternalArticle: true,
        content: content.substring(0, 5000), // Primeros 5000 chars como contexto
        issues: analysis.issues,
        useful: useful,
        totalIssuesFound: 
          analysis.issues.outdatedSources.length +
          analysis.issues.fictionalExamples.length +
          analysis.issues.externalRecommendations.length +
          analysis.issues.inappropriateTerms.length
      };

    } catch (e) {
      console.log('No external article provided or error loading:', e.message);
      return null;
    }
  }

  // Procesar datos principales
  process() {
    try {
      // Analizar art√≠culo externo si existe
      const externalAnalysis = this.analyzeExternalArticle();

      // Capturar todos los datos
      const getData = (nodeName) => {
        try {
          return $items(nodeName).map(item => item.json);
        } catch (e) {
          this.warnings.push(`No data from ${nodeName}`);
          return [];
        }
      };

      const brandBase = getData('Load Brand Base');
      const faqs = getData('Load FAQs');
      const interactions = getData('Load Interactions');
      const blogs = getData('Load All Blogs');
      const videos = getData('Load All Videos');
      const videoMap = getData('Load Blog/Video Map');

      // Procesar intake
      const webhookData = $items('Webhook Entry')[0].json;
      const rawIntake = webhookData.body?.intake || webhookData.body || {};

      const intake = {
        topic: this.fixEncoding(rawIntake.topic || ''),
        language: (rawIntake.language || 'ES').toUpperCase(),
        centralTheme: this.fixEncoding(rawIntake.central_theme || ''),
        primaryKw: this.fixEncoding(rawIntake.primary_kw || ''),
        secondaryKws: (rawIntake.secondary_kws || []).map(k => this.fixEncoding(k)),
        description: this.fixEncoding(rawIntake.description || ''),
        suggestedTitle: this.fixEncoding(rawIntake.suggested_title || ''),
        suggestedSlug: (rawIntake.suggested_slug || '').toLowerCase().replace(/[^a-z0-9]+/g, '-'),
        internalSources: rawIntake.internal_sources || [],
        wordCount: rawIntake.word_count || 3000,
        useExternalArticle: Boolean(rawIntake.externalArticleId)
      };

      // Procesar Brand Data con validaciones
      const brandData = {};
      brandBase.forEach(row => {
        const key = (row.key || '').toLowerCase().replace(/\s+/g, '_');
        const value = this.fixEncoding(row.value || '');
        if (key && value) brandData[key] = value;
      });

      // An√°lisis sem√°ntico mejorado
      const keywordBag = [
        intake.topic,
        intake.centralTheme,
        intake.primaryKw,
        ...intake.secondaryKws
      ].filter(Boolean);

      // Score de relevancia
      const scoreRelevance = (text, keywords) => {
        const textLower = String(text || '').toLowerCase();
        let score = 0;
        keywords.forEach(kw => {
          const kwLower = String(kw || '').toLowerCase();
          if (textLower.includes(kwLower)) score += 10;
          const words = kwLower.split(/\s+/);
          words.forEach(word => {
            if (word.length > 3 && textLower.includes(word)) score += 3;
          });
        });
        return score;
      };

      // Analizar FAQs
      const analyzedFaqs = faqs.map(faq => ({
        ...faq,
        relevanceScore: scoreRelevance(
          `${faq.theme || ''} ${faq.question || ''} ${faq.answer || ''}`,
          keywordBag
        )
      })).sort((a, b) => b.relevanceScore - a.relevanceScore);

      // Analizar interacciones
      const analyzedInteractions = interactions.map(inter => ({
        ...inter,
        relevanceScore: scoreRelevance(
          `${inter.q || ''} ${inter.a || ''}`,
          keywordBag
        )
      })).sort((a, b) => b.relevanceScore - a.relevanceScore);

      // Detectar duplicados
      const existingSlugs = blogs.map(b => (b.slug || '').toLowerCase());
      const existingTitles = blogs.map(b => (b.title || '').toLowerCase());

      const duplicates = {
        exactSlug: existingSlugs.includes(intake.suggestedSlug),
        exactTitle: existingTitles.includes(intake.suggestedTitle.toLowerCase())
      };

      // Video matching
      const findBestVideo = () => {
        const mapEntry = videoMap.find(m => 
          m.blog_slug === intake.suggestedSlug ||
          m.blog_title === intake.suggestedTitle
        );

        if (mapEntry) {
          return videos.find(v => v.id === mapEntry.video_id);
        }

        const scoredVideos = videos.map(video => ({
          ...video,
          relevanceScore: scoreRelevance(
            `${video.title || ''} ${video.description || ''}`,
            keywordBag
          )
        })).sort((a, b) => b.relevanceScore - a.relevanceScore);

        return scoredVideos[0]?.relevanceScore > 30 ? scoredVideos[0] : null;
      };

      const bestVideo = findBestVideo();

      // Blogs relacionados
      const analyzedBlogs = blogs.map(blog => ({
        ...blog,
        relevanceScore: scoreRelevance(
          `${blog.title || ''} ${blog.slug || ''}`,
          keywordBag
        )
      })).filter(b => b.relevanceScore > 0)
        .sort((a, b) => b.relevanceScore - a.relevanceScore);

      // Construir contexto masivo con reglas actualizadas
      const brandContext = {
        identity: {
          mission: brandData.mission || '',
          vision: brandData.vision || '',
          elevator: brandData.elevator || '',
          values: (brandData.values || '').split(',').map(v => v.trim())
        },

        voice: {
          register: brandData.voice_register || 'Cercano, educativo y profesional',
          tone: brandData.voice_tone || 'Emp√°tico y claro',
          style: brandData.voice_style || 'Directo con ejemplos pr√°cticos',
          dos: (brandData.voice_dos || '').split(';').map(d => d.trim()),
          donts: (brandData.voice_donts || '').split(';').map(d => d.trim())
        },

        rules: {
          bannedTerms: ['indocumentado', 'ilegal', 'sin papeles', 'garantizado', 'sin riesgo'],
          requiredDisclaimer: 'Este contenido es educativo y no constituye asesor√≠a financiera personalizada.',
          yearRestriction: 'SOLO usar datos de 2025 en adelante',
          exampleRule: 'SIEMPRE aclarar que los ejemplos son ficticios usando "Imaginemos a..."',
          internalOnly: 'SOLO recomendar herramientas de Finhabits, nunca competidores',
          linkingRule: 'SIEMPRE enlazar a gu√≠as internas de Finhabits'
        },

        seo: {
          pillars: ['/es/ahorro', '/es/credito', '/es/inversion', '/es/presupuesto', '/es/emma'],
          internalTools: ['Emma', 'Calculadora de jubilaci√≥n', 'Herramientas Finhabits'],
          youtubeChannel: 'Canal de YouTube de Finhabits',
          app: 'App de Finhabits'
        },

        content: {
          topFaqs: analyzedFaqs.slice(0, 20),
          topInteractions: analyzedInteractions.slice(0, 20),
          relatedBlogs: analyzedBlogs.slice(0, 15),
          suggestedVideo: bestVideo,
          duplicateWarning: duplicates
        },

        externalArticleAnalysis: externalAnalysis,

        corrections: externalAnalysis ? {
          mustFix: externalAnalysis.issues,
          totalCorrectionsNeeded: externalAnalysis.totalIssuesFound
        } : null
      };

      return [{
        json: {
          success: true,
          intake,
          brandContext,
          rawData: {
            faqs: analyzedFaqs,
            interactions: analyzedInteractions,
            blogs: analyzedBlogs,
            videos
          },
          metadata: {
            timestamp: new Date().toISOString(),
            version: '6.0',
            hasExternalReference: Boolean(externalAnalysis),
            issuesDetected: externalAnalysis?.totalIssuesFound || 0,
            warnings: this.warnings
          }
        }
      }];

    } catch (error) {
      return [{
        json: {
          success: false,
          error: error.message,
          stack: error.stack
        }
      }];
    }
  }
}

const orchestrator = new QuantumOrchestratorV6();
return orchestrator.process();

