class ValidatorProcessor {
  constructor() {
    this.validations = {};
    this.corrections = [];
  }

  getContent(source) {
    try {
      const data = $items(source)[0].json;
      return data.output || data.text || data.content || '';
    } catch (e) {
      console.log(`No content from ${source}`);
      return '';
    }
  }

  validateContent(content, rules) {
    const validations = {
      // Validaciones cr√≠ticas de las reglas
      has2025Data: {
        check: () => {
          const oldDates = content.match(/\b(202[0-4])\b/g) || [];
          return oldDates.length === 0;
        },
        score: 0,
        issue: 'Contiene fechas anteriores a 2025',
        correction: 'Actualizar todas las fechas a 2025 o posterior'
      },

      hasFictionalExamples: {
        check: () => {
          const names = ['Mar√≠a', 'Carlos', 'Jos√©', 'Ana', 'Carmen'];
          for (const name of names) {
            const pattern = new RegExp(`${name}(?!.*imaginemos)(?!.*ficticio)`, 'gi');
            if (pattern.test(content)) return false;
          }
          return true;
        },
        score: 0,
        issue: 'Ejemplos no clarificados como ficticios',
        correction: 'Agregar "Imaginemos a" antes de cada nombre de ejemplo'
      },

      noExternalTools: {
        check: () => {
          const competitors = ['Vanguard', 'Fidelity', 'Schwab', 'Betterment', 'Wealthfront'];
          return !competitors.some(c => content.includes(c));
        },
        score: 0,
        issue: 'Menciona herramientas de competidores',
        correction: 'Reemplazar con herramientas de Finhabits (Emma, App, Calculadora)'
      },

      appropriateLanguage: {
        check: () => {
          const banned = ['indocumentado', 'ilegal', 'sin papeles'];
          return !banned.some(b => content.toLowerCase().includes(b));
        },
        score: 0,
        issue: 'Usa lenguaje inapropiado',
        correction: 'Cambiar a "personas con ITIN" o "quienes necesitan documentaci√≥n"'
      },

      hasInternalLinks: {
        check: () => {
          const links = content.match(/\[([^\]]+)\]\(([^)]+)\)/g) || [];
          const internalLinks = links.filter(l => l.includes('/es/'));
          return internalLinks.length >= 15;
        },
        score: 0,
        issue: 'Menos de 15 enlaces internos',
        correction: 'Agregar m√°s enlaces a contenido de Finhabits'
      },

      promotesFinhabits: {
        check: () => {
          return content.includes('Emma') && 
                 content.includes('Finhabits') &&
                 (content.includes('app') || content.includes('App'));
        },
        score: 0,
        issue: 'No promociona suficientemente herramientas Finhabits',
        correction: 'Agregar secci√≥n sobre Emma, App y herramientas de Finhabits'
      },

      hasDisclaimer: {
        check: () => content.includes('asesor√≠a financiera'),
        score: 0,
        issue: 'Falta disclaimer legal',
        correction: 'Agregar disclaimer completo al final'
      },

      hasProperStructure: {
        check: () => {
          return content.includes('#') && 
                 content.includes('##') &&
                 content.includes('FAQ') &&
                 content.split(/\s+/).length >= rules.wordCount;
        },
        score: 0,
        issue: 'Estructura incompleta',
        correction: 'Asegurar H1, H2s, FAQ y longitud m√≠nima'
      }
    };

    // Ejecutar validaciones
    Object.keys(validations).forEach(key => {
      validations[key].score = validations[key].check() ? 100 : 0;
      if (validations[key].score === 0) {
        this.corrections.push({
          type: key,
          issue: validations[key].issue,
          correction: validations[key].correction,
          critical: ['has2025Data', 'hasFictionalExamples', 'appropriateLanguage'].includes(key)
        });
      }
    });

    const totalScore = Object.values(validations).reduce((sum, v) => sum + v.score, 0) / Object.keys(validations).length;

    return {
      validations,
      score: Math.round(totalScore),
      passed: totalScore >= 80,
      corrections: this.corrections
    };
  }

  process() {
    try {
      // Obtener datos del prompt builder
      const promptData = $items('Ultra Prompt Builder v6')[0].json;
      const rules = promptData.prompts.validationRules;

      // Obtener contenidos de ambos LLMs
      const openaiContent = this.getContent('OpenAI Writer');
      const anthropicContent = this.getContent('Anthropic Writer');

      // Validar ambos contenidos
      const openaiValidation = this.validateContent(openaiContent, {
        ...rules,
        wordCount: promptData.intake.wordCount
      });

      const anthropicValidation = this.validateContent(anthropicContent, {
        ...rules,
        wordCount: promptData.intake.wordCount
      });

      // Seleccionar el mejor contenido
      const bestContent = openaiValidation.score >= anthropicValidation.score ? 
        { content: openaiContent, source: 'openai', validation: openaiValidation } :
        { content: anthropicContent, source: 'anthropic', validation: anthropicValidation };

      // Preparar datos para el optimizer final
      return [{
        json: {
          bestContent,
          openai: {
            content: openaiContent,
            validation: openaiValidation
          },
          anthropic: {
            content: anthropicContent,
            validation: anthropicValidation
          },
          needsOptimization: !bestContent.validation.passed,
          criticalCorrections: bestContent.validation.corrections.filter(c => c.critical),
          allCorrections: bestContent.validation.corrections,
          promptData
        }
      }];

    } catch (error) {
      return [{
        json: {
          error: error.message,
          stack: error.stack
        }
      }];
    }
  }
}

const validator = new ValidatorProcessor();
