class FinalOutputProcessor {

  extractContent(source) {
    try {
      const data = $items(source)[0].json;
      return data.output || data.text || data.content || '';
    } catch (e) {
      // Si el optimizer no corri√≥, usar el mejor contenido original
      try {
        const validatorData = $items('Validator Processor')[0].json;
        return validatorData.bestContent.content;
      } catch (e2) {
        throw new Error('No se pudo obtener contenido final');
      }
    }
  }

  finalValidation(content) {
    const checks = {
      no2024Data: !(content.match(/\b2024\b/g) || []).length,
      fictionalClear: !content.match(/(?:Mar√≠a|Carlos|Jos√©)(?!.*[Ii]magin)/g),
      noCompetitors: !['Vanguard', 'Fidelity', 'Schwab'].some(c => content.includes(c)),
      hasEmma: content.includes('Emma'),
      hasFinhabits: content.includes('Finhabits'),
      hasDisclaimer: content.includes('asesor√≠a financiera'),
      minLength: content.split(/\s+/).length >= 2500
    };

    const score = Object.values(checks).filter(Boolean).length / Object.keys(checks).length * 100;

    return {
      score: Math.round(score),
      passed: score >= 85,
      checks
    };
  }

  process() {
    try {
      // Obtener contenido final optimizado
      const finalContent = this.extractContent('Final Optimizer LLM');

      // Validaci√≥n final
      const validation = this.finalValidation(finalContent);

      // Extraer metadata
      const title = (finalContent.match(/^#\s+(.+)/m) || ['', 'Art√≠culo Finhabits'])[1];
      const wordCount = finalContent.split(/\s+/).length;

      // Obtener datos originales
      const validatorData = $items('Validator Processor')[0].json;
      const promptData = validatorData.promptData;

      return [{
        json: {
          success: true,
          content: {
            body: finalContent,
            title: title,
            slug: promptData.intake.suggestedSlug || title.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
            wordCount: wordCount
          },
          validation: {
            final: validation,
            original: validatorData.bestContent.validation,
            improved: validation.score > validatorData.bestContent.validation.score
          },
          metadata: {
            version: '6.0',
            timestamp: new Date().toISOString(),
            usedExternalArticle: Boolean(promptData.brandContext.externalArticleAnalysis),
            correctionsApplied: validatorData.allCorrections.length,
            llmsUsed: ['openai', 'anthropic', 'claude-optimizer']
          },
          quality: {
            status: validation.passed ? 'APPROVED' : 'NEEDS_REVIEW',
            score: validation.score,
            message: validation.passed ? 
              'Contenido aprobado y listo para publicaci√≥n' : 
              'Contenido requiere revisi√≥n manual adicional'
          }
        }
      }];

    } catch (error) {
      return [{
        json: {
          success: false,
          error: error.message
        }
      }];
    }
  }
}

const processor = new FinalOutputProcessor();
return processor.process();
